function diffArray(arr1, arr2) {
  var newArr = [];
  var mergeArr = arr1.concat(arr2);
  mergeArr.sort();

  for(var i = 0; i < mergeArr.length; i++){

    if
    (mergeArr.indexOf(mergeArr[i],  
    mergeArr.indexOf(mergeArr[i]) + 1) === -1)
    {
      newArr.push(mergeArr[i]);
    }
  } 
  return newArr;
}

diffArray([1, 2, 3, 5], [1, 2, 3, 4, 5]);
