function diffArray(arr1, arr2) {

      // 36 minutes, 26 seconds
      let uniqueArr = [];
      var i;
      var j;

      for(i = 0; i < arr2.length; i++){
            if(arr1.indexOf(arr2[i]) === -1){
                  uniqueArr.push(arr2[i]);
            }
      }

      for(j = 0; j < arr1.length; j++){
            if(arr2.indexOf(arr1[j]) === - 1){
                  uniqueArr.push(arr1[j]); 
            }
      }

      return uniqueArr;
}

