function spinalCase(str) {
  // "It's such a fine line between stupid, and clever."
  // --David St. Hubbins

  var i;
  var res;

  for(i = 0; i < str.length; i++){
      res = 
       str.replace(/[\s_]/g, '-')
      .replace(/([a-z])([A-Z])/g, '$1-$2')
      .toLowerCase();
  }

  return res;
}

spinalCase('This Is Spinal Tap');
